$(document).ready(function(){
	//初始化
});
//等待效果
function waiting(){
  $('body').append('<div class="load_effect"></div>');
}
//移除等待效果
function complete(){
  $('.load_effect').remove();
}
//手机端消息提示
function showInfo(msg, type, fun1, fun2){
	var type = arguments[1] ? arguments[1] : 1, fun1 = arguments[2] ? arguments[2] : null, fun2 = arguments[3] ? arguments[3] : null, html = '<div class="show_msg_bg"></div><div class="show_msg_div"><span>' + msg + '</span>';
	if(type == 1){
		html += '<button class="btn1 show_msg_ok">确定</button>';
	}else{
		html += '<p class="btn"><button class="btn2 show_msg_ok">确定</button><button class="btn2 show_msg_cancel">取消</button></p>';
	}
	html += '</div>';
	$('body').append(html);
	//确定事件处理
	$('.show_msg_div .show_msg_ok').click(function(){
		fun1 && fun1(); hideInfo();
	});
	//取消事件处理
	$('.show_msg_div .show_msg_cancel').click(function(){
		fun2 && fun2(); hideInfo();
	});
}
//关闭消息提示
function hideInfo(){
	$('.show_msg_bg,.show_msg_div').remove();
}